#this code is shamelessly taken from https://b3d.interplanety.org/en/creating-multifile-add-on-for-blender/bl_info = {    "name": "MCEdit2Blender",    "description": "A Blender addon to quickly import Minecraft worlds using MCEdit .schematic files.",    "author": "scribblemaniac, jlsajfj",    "blender": (2, 91, 2),    "version": (1, 0),    "support": "TESTING",    "category": "Object",    "wiki_url": "https://github.com/scribblemaniac/MCEdit2Blender",}modulesNames = ['blockmanager', 'Addon']modulesFullNames = []for currentModuleName in modulesNames:    modulesFullNames.append('{}.{}'.format(__name__, currentModuleName))import sysimport importlib print(modulesNames)print(modulesFullNames)for currentModuleFullName in modulesFullNames:    if currentModuleFullName in sys.modules:        importlib.reload(sys.modules[currentModuleFullName])    else:        globals()[currentModuleFullName] = importlib.import_module(currentModuleFullName)        setattr(globals()[currentModuleFullName], 'modulesNames', modulesFullNames) def register():    for currentModuleName in modulesFullNames:        if currentModuleName in sys.modules:            if hasattr(sys.modules[currentModuleName], 'register'):                sys.modules[currentModuleName].register() def unregister():    for currentModuleName in modulesFullNames:        if currentModuleName in sys.modules:            if hasattr(sys.modules[currentModuleName], 'unregister'):                sys.modules[currentModuleName].unregister() if __name__ == "__main__":    register()